import React from 'react';
import { Box, Typography, Button, Grid } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Home = ({ notes = [] }) => {  // Default value for notes to ensure it's never undefined
  const navigate = useNavigate();

  return (
    <Box
      sx={{
        padding: '20px',
        textAlign: 'center',
        backgroundColor: 'var(--background-color)',
        color: 'var(--text-color)',
        minHeight: '100vh',  // Ensure full page height
      }}
    >
      <Typography variant="h3" gutterBottom>
        Your Notes
      </Typography>

      {/* Check if notes are defined and have content */}
      {notes && notes.length > 0 ? (
        <Grid container spacing={4} justifyContent="center">
          {notes.map(note => (
            <Grid item xs={12} sm={6} md={4} key={note._id}>
              <Box
                sx={{
                  padding: '20px',
                  backgroundColor: 'var(--box-background-color)',
                  borderRadius: '8px',
                  boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
                  textAlign: 'left',
                }}
              >
                <Typography variant="h6" sx={{ marginBottom: '10px' }}>
                  {note.title}
                </Typography>
                <Typography variant="body2" sx={{ marginBottom: '10px' }}>
                  {note.content}
                </Typography>

                {/* Edit Button */}
                <Button
                  variant="outlined"
                  onClick={() => navigate(`/edit/${note._id}`)}  // Navigate to edit page for the specific note
                  sx={{ marginTop: '10px' }}
                >
                  Edit
                </Button>
              </Box>
            </Grid>
          ))}
        </Grid>
      ) : (
        <Typography>You currently have no notes.</Typography>
      )}

      {/* Add New Note Button */}
      <Button
        variant="contained"
        color="primary"
        onClick={() => navigate('/create')}
        sx={{ marginTop: '30px' }}
      >
        Add New Note
      </Button>
    </Box>
  );
};

export default Home;
