import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from './components/Home';
import CreateNote from './components/CreateNote';
import EditNote from './components/EditNote';  // Import the EditNote component
import Signup from './components/Signup';
import Login from './components/Login';
import PrivateRoute from './components/PrivateRoute';  // PrivateRoute for authentication

const App = () => {
  const [darkMode, setDarkMode] = useState(false);

  // Apply dark mode globally by updating CSS variables
  useEffect(() => {
    if (darkMode) {
      document.documentElement.style.setProperty('--background-color', '#121212');
      document.documentElement.style.setProperty('--text-color', '#e0e0e0');
      document.documentElement.style.setProperty('--box-background-color', '#1e1e1e');
      document.documentElement.style.setProperty('--button-color', '#bb86fc');
    } else {
      document.documentElement.style.setProperty('--background-color', '#f5f5f5');
      document.documentElement.style.setProperty('--text-color', '#333');
      document.documentElement.style.setProperty('--box-background-color', '#ffffff');
      document.documentElement.style.setProperty('--button-color', '#1976d2');
    }
  }, [darkMode]);

  return (
    <Router>
      <div style={{ backgroundColor: 'var(--background-color)', minHeight: '100vh', color: 'var(--text-color)' }}>
        {/* Dark mode toggle can be added here globally */}
        <Routes>
          {/* Public Routes */}
          <Route path="/signup" element={<Signup />} />
          <Route path="/login" element={<Login />} />

          {/* Private Routes */}
          <Route path="/" element={<PrivateRoute><Home darkMode={darkMode} setDarkMode={setDarkMode} /></PrivateRoute>} />
          <Route path="/create" element={<PrivateRoute><CreateNote darkMode={darkMode} setDarkMode={setDarkMode} /></PrivateRoute>} />
          <Route path="/edit/:id" element={<PrivateRoute><EditNote darkMode={darkMode} setDarkMode={setDarkMode} /></PrivateRoute>} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
