import React, { useEffect, useState } from 'react';
import { AppBar, Toolbar, Typography, Box, Button, Switch } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Dashboard = ({ darkMode, setDarkMode }) => {
  const [notes, setNotes] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchNotes = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/notes', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const data = await response.json();
        if (response.ok) {
          setNotes(data);
        } else {
          console.error('Failed to fetch notes:', data.message);
        }
      } catch (error) {
        console.error('Error fetching notes:', error);
      }
    };

    fetchNotes();
  }, []);

  return (
    <Box sx={{ height: '100vh', display: 'flex', flexDirection: 'column' }}>
      <AppBar position="static" color="primary">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Jeremy's Notes App
          </Typography>
          {/* Global Dark Mode Toggle */}
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Typography variant="body2" sx={{ color: 'inherit', marginRight: 1 }}>Dark Mode</Typography>
            <Switch
              checked={darkMode}
              onChange={() => setDarkMode(!darkMode)}
              color="default"
            />
          </Box>
        </Toolbar>
      </AppBar>

      <Box
        sx={{
          flexGrow: 1,
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'flex-start',
          alignItems: 'center',
          padding: '20px',
          backgroundColor: 'var(--background-color)',
        }}
      >
        <Typography variant="h4" gutterBottom sx={{ color: 'var(--text-color)' }}>
          Your Notes
        </Typography>

        <Box
          sx={{
            width: '100%',
            maxWidth: '800px',
            backgroundColor: 'var(--box-background-color)',
            padding: '20px',
            borderRadius: '8px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
            marginTop: '20px',
          }}
        >
          {notes.length > 0 ? (
            notes.map((note, index) => (
              <Box key={index} sx={{ marginBottom: '10px' }}>
                <Typography variant="h6" sx={{ color: 'var(--text-color)' }}>{note.title}</Typography>
                <Typography variant="body2" sx={{ color: 'var(--text-color)' }}>{note.content}</Typography>
              </Box>
            ))
          ) : (
            <Typography variant="body1" color="textSecondary">
              You currently have no notes.
            </Typography>
          )}
        </Box>

        <Button
          variant="contained"
          color="primary"
          sx={{ marginTop: '20px', backgroundColor: 'var(--button-color)' }}
          onClick={() => navigate('/create')}
        >
          Add New Note
        </Button>
      </Box>
    </Box>
  );
};

export default Dashboard;
