import React, { useState } from 'react';
import { TextField, Button, Container, Typography, AppBar, Toolbar, Box, Switch } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const CreateNote = ({ darkMode, setDarkMode }) => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const navigate = useNavigate();

  const handleCreateNote = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    await fetch('http://localhost:5000/api/notes', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ title, content }),
    });

    navigate('/');
  };

  return (
    <Box sx={{ height: '100vh', display: 'flex', flexDirection: 'column' }}>
      <AppBar position="static" color="primary">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Jeremy's Notes App
          </Typography>
          {/* Global Dark Mode Toggle */}
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Typography variant="body2" sx={{ color: 'inherit', marginRight: 1 }}>Dark Mode</Typography>
            <Switch
              checked={darkMode}
              onChange={() => setDarkMode(!darkMode)}
              color="default"
            />
          </Box>
        </Toolbar>
      </AppBar>

      <Container sx={{ paddingTop: '20px' }}>
        <Typography variant="h4" gutterBottom>Create a New Note</Typography>
        <form onSubmit={handleCreateNote}>
          <TextField
            label="Title"
            fullWidth
            margin="normal"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <TextField
            label="Content"
            fullWidth
            margin="normal"
            multiline
            rows={4}
            value={content}
            onChange={(e) => setContent(e.target.value)}
          />
          <Button variant="contained" type="submit" sx={{ marginTop: '20px' }}>
            Create Note
          </Button>
        </form>
      </Container>
    </Box>
  );
};

export default CreateNote;
