import React from 'react';
import { AppBar, Toolbar, Typography, Container } from '@mui/material';
import { Outlet, useLocation } from 'react-router-dom';

const Layout = () => {
  const location = useLocation();
  const isLoggedIn = !!localStorage.getItem('token');  // Check if the user is logged in

  return (
    <>
      {/* Conditionally render AppBar if not on login or signup pages */}
      {isLoggedIn && !['/login', '/signup'].includes(location.pathname) && (
        <AppBar position="static">
          <Toolbar>
            <Container maxWidth="lg" style={{ display: 'flex', justifyContent: 'center' }}>
              <Typography variant="h6" align="center">
                Jeremy's Notes App
              </Typography>
            </Container>
          </Toolbar>
        </AppBar>
      )}

      {/* Main content goes here */}
      <Container maxWidth="lg" style={{ marginTop: isLoggedIn ? '40px' : '0' }}>
        <Outlet />
      </Container>
    </>
  );
};

export default Layout;
