import React, { useState } from 'react';
import { TextField, Button, Container, Typography, Box, Link } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:5000/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password }),
    });

    const data = await response.json();
    if (response.ok) {
      localStorage.setItem('token', data.token);  // Store JWT token
      navigate('/');  // Redirect to home
    } else {
      console.error('Login failed:', data.message);
    }
  };

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',  // Ensures full-page vertical centering
        textAlign: 'center',  // Center the text inside the form
      }}
    >
      <Box
        sx={{
          width: '100%',
          maxWidth: '400px',  // Set a maximum width to prevent stretching
          padding: '20px',
          borderRadius: '8px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',  // Add a subtle shadow for styling
        }}
      >
        <Typography variant="h4" gutterBottom>
          Login
        </Typography>
        <form onSubmit={handleLogin}>
          <TextField
            label="Username"
            fullWidth
            margin="normal"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            inputProps={{ style: { fontSize: 18, padding: '10px' } }}  // Increase font size and padding
            InputLabelProps={{ style: { fontSize: 18 } }}  // Adjust label font size
          />
          <TextField
            label="Password"
            type="password"
            fullWidth
            margin="normal"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            inputProps={{ style: { fontSize: 18, padding: '10px' } }}  // Increase font size and padding
            InputLabelProps={{ style: { fontSize: 18 } }}  // Adjust label font size
          />
          <Button
            variant="contained"
            color="primary"
            type="submit"
            fullWidth
            style={{ marginTop: '20px', padding: '10px', fontSize: '18px' }}
          >
            Login
          </Button>
        </form>
        <Link href="/signup" style={{ marginTop: '20px', display: 'block', fontSize: '16px' }}>
          Create an Account
        </Link>
      </Box>
    </Box>
  );
};

export default Login;
