import React from 'react';
import { Box, Typography, Button, Grid, Switch } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Home = ({ notes = [], darkMode, setDarkMode }) => {
  const navigate = useNavigate();

  return (
    <Box
      sx={{
        backgroundColor: 'var(--background-color)',
        color: 'var(--text-color)',
        minHeight: '100vh',  // Ensure full page height
      }}
    >
      {/* Header with dark mode toggle */}
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '16px',
          width: '80%',  // Make header 80% of the screen width
          margin: '0 auto',  // Center the header horizontally
          backgroundColor: 'var(--box-background-color)',  // Header color
          boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',  // Shadow under header
        }}
      >
        <Typography variant="h6">Jeremy's Notes App</Typography>
        <Box display="flex" alignItems="center">
          <Typography variant="body1" sx={{ marginRight: 1 }}>
            Dark Mode
          </Typography>
          <Switch checked={darkMode} onChange={() => setDarkMode(!darkMode)} />
        </Box>
      </Box>

      {/* Main content */}
      <Box sx={{ padding: '20px', textAlign: 'center' }}>
        <Typography variant="h3" gutterBottom>
          Your Notes
        </Typography>

        {/* Check if notes are defined and have content */}
        {notes && notes.length > 0 ? (
          <Grid container spacing={4} justifyContent="center">
            {notes.map(note => (
              <Grid item xs={12} sm={6} md={4} key={note._id}>
                <Box
                  sx={{
                    padding: '20px',
                    backgroundColor: 'var(--box-background-color)',
                    borderRadius: '8px',
                    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
                    textAlign: 'left',
                  }}
                >
                  <Typography variant="h6" sx={{ marginBottom: '10px' }}>
                    {note.title}
                  </Typography>
                  <Typography variant="body2" sx={{ marginBottom: '10px' }}>
                    {note.content}
                  </Typography>

                  {/* Edit Button */}
                  <Button
                    variant="outlined"
                    onClick={() => navigate(`/edit/${note._id}`)}
                    sx={{ marginTop: '10px' }}
                  >
                    Edit
                  </Button>
                </Box>
              </Grid>
            ))}
          </Grid>
        ) : (
          <Typography>You currently have no notes.</Typography>
        )}

        {/* Add New Note Button */}
        <Button
          variant="contained"
          color="primary"
          onClick={() => navigate('/create')}
          sx={{ marginTop: '30px' }}
        >
          Add New Note
        </Button>
      </Box>
    </Box>
  );
};

export default Home;
