import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Typography, Box, Button, Switch } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Dashboard = ({ darkMode, setDarkMode }) => {
  const [notes, setNotes] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchNotes = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/notes', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const data = await response.json();
        if (response.ok) {
          setNotes(data);
        } else {
          console.error('Failed to fetch notes:', data.message);
        }
      } catch (error) {
        console.error('Error fetching notes:', error);
      }
    };

    fetchNotes();
  }, []);

  return (
    <Box sx={{ height: '100vh', display: 'flex', flexDirection: 'column' }}>
      {/* Single AppBar */}
      <AppBar
        position="static"
        sx={{
          backgroundColor: darkMode ? '#6a0dad' : 'primary.main',
          color: darkMode ? '#fff' : '#fff',
        }}
      >
        <Toolbar>
          {/* Header text on the left */}
          <Typography variant="h6" component="div" sx={{ flexGrow: 1, textAlign: 'left' }}>
            Jeremy's Notes App
          </Typography>

          {/* Dark mode toggle on the right */}
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Typography variant="body2" sx={{ color: 'inherit', marginRight: 1 }}>Dark Mode</Typography>
            <Switch
              checked={darkMode}
              onChange={() => setDarkMode(!darkMode)}
              color="default"
            />
          </Box>
        </Toolbar>
      </AppBar>

      {/* Content */}
      <Box
        sx={{
          flexGrow: 1,
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'flex-start',
          alignItems: 'center',
          padding: '40px',
          backgroundColor: 'var(--background-color)',
        }}
      >
        <Typography variant="h3" gutterBottom sx={{ color: 'var(--text-color)', marginBottom: '40px' }}>
          Your Notes
        </Typography>

        <Box
          sx={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
            gap: '20px',
            width: '100%',
            maxWidth: '1200px',
          }}
        >
          {notes.length > 0 ? (
            notes.map((note, index) => (
              <Box key={index} sx={{ backgroundColor: 'var(--box-background-color)', padding: '20px', borderRadius: '12px', boxShadow: '0 4px 12px rgba(0,0,0,0.1)' }}>
                <Typography variant="h6" sx={{ color: 'var(--text-color)' }}>{note.title}</Typography>
                <Typography variant="body2" sx={{ color: 'var(--text-color)', marginTop: '10px' }}>{note.content}</Typography>
              </Box>
            ))
          ) : (
            <Typography variant="body1" color="textSecondary">
              You currently have no notes.
            </Typography>
          )}
        </Box>

        <Button
          variant="contained"
          color="primary"
          sx={{ marginTop: '40px', padding: '10px 20px', backgroundColor: 'var(--button-color)', fontSize: '16px' }}
          onClick={() => navigate('/create')}
        >
          Add New Note
        </Button>
      </Box>
    </Box>
  );
};

export default Dashboard;
