import React, { useState } from 'react';
import { TextField, Button, Container, Typography, AppBar, Toolbar, Box, Switch } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const CreateNote = ({ darkMode, setDarkMode }) => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const navigate = useNavigate();

  const handleCreateNote = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    await fetch('http://localhost:5000/api/notes', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ title, content }),
    });

    navigate('/');
  };

  return (
    <Box sx={{ height: '100vh', display: 'flex', flexDirection: 'column' }}>
      {/* Single AppBar with Header and Dark Mode Toggle */}
      <AppBar
        position="static"
        sx={{
          backgroundColor: darkMode ? '#6a0dad' : 'primary.main',
          color: darkMode ? '#fff' : '#fff',
        }}
      >
        <Toolbar>
          {/* Header text on the left */}
          <Typography variant="h6" sx={{ flexGrow: 1, textAlign: 'left' }}>
            Jeremy's Notes App
          </Typography>

          {/* Dark mode toggle on the right */}
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Typography variant="body2" sx={{ color: 'inherit', marginRight: 1 }}>Dark Mode</Typography>
            <Switch
              checked={darkMode}
              onChange={() => setDarkMode(!darkMode)}
              color="default"
            />
          </Box>
        </Toolbar>
      </AppBar>

      <Container sx={{ paddingTop: '40px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <Typography variant="h4" gutterBottom sx={{ marginBottom: '30px', color: 'var(--text-color)' }}>Create a New Note</Typography>
        <form onSubmit={handleCreateNote} style={{ width: '100%', maxWidth: '600px' }}>
          <TextField
            label="Title"
            fullWidth
            margin="normal"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            InputProps={{ style: { color: 'var(--text-color)' } }}
            InputLabelProps={{ style: { color: 'var(--text-color)' } }}
            sx={{ marginBottom: '20px' }}
          />
          <TextField
            label="Content"
            fullWidth
            margin="normal"
            multiline
            rows={6}
            value={content}
            onChange={(e) => setContent(e.target.value)}
            InputProps={{ style: { color: 'var(--text-color)' } }}
            InputLabelProps={{ style: { color: 'var(--text-color)' } }}
            sx={{ marginBottom: '30px' }}
          />
          <Button variant="contained" type="submit" sx={{ padding: '10px 20px', backgroundColor: 'var(--button-color)' }}>
            Create Note
          </Button>
        </form>
      </Container>
    </Box>
  );
};

export default CreateNote;
