import React, { useState, useEffect } from 'react';
import { Container, Typography, Button, Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const [notes, setNotes] = useState([]);  // Store fetched notes
  const navigate = useNavigate();

  // Fetch notes when the component mounts
  useEffect(() => {
    const fetchNotes = async () => {
      const token = localStorage.getItem('token');  // Get the token from local storage
      const response = await fetch('http://localhost:5000/api/notes', {
        headers: {
          'Authorization': `Bearer ${token}`  // Pass the token for authentication
        }
      });
      const data = await response.json();
      setNotes(data);  // Store the fetched notes in state
    };

    fetchNotes();
  }, []);

  // Handle navigation to the "Create Note" page
  const handleCreateNote = () => {
    navigate('/create');
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>Your Notes</Typography>
      {notes.length > 0 ? (
        <Box>
          {notes.map((note) => (
            <Box key={note._id} sx={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px', borderRadius: '8px' }}>
              <Typography variant="h6">{note.title}</Typography>
              <Typography variant="body1">{note.content}</Typography>
              <Button onClick={() => navigate(`/edit/${note._id}`)}>Edit</Button>
            </Box>
          ))}
        </Box>
      ) : (
        <Typography variant="body1">You currently have no notes.</Typography>
      )}

      <Button
        variant="contained"
        color="primary"
        onClick={handleCreateNote}
        sx={{ marginTop: '20px' }}
      >
        Add New Note
      </Button>
    </Container>
  );
};

export default Home;
