import React, { useState } from 'react';
import { TextField, Button, Container, Typography, Grid, Link, Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:5000/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password }),
    });

    const data = await response.json();
    if (response.ok) {
      localStorage.setItem('token', data.token);  // Store JWT token
      navigate('/');  // Redirect to home
    } else {
      console.error('Login failed:', data.message);
    }
  };

  return (
    <Container maxWidth="sm">
      <Grid
        container
        direction="column"
        justifyContent="center"
        alignItems="center"
        style={{ minHeight: '100vh' }}  // Ensures full-page vertical centering
      >
        <Box width="100%" maxWidth="400px">  {/* Limit width of the login box */}
          <Typography variant="h4" gutterBottom align="center">  {/* Center the text */}
            Login
          </Typography>
          <form onSubmit={handleLogin} style={{ width: '100%' }}>
            <TextField
              label="Username"
              fullWidth
              margin="normal"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              inputProps={{ style: { fontSize: 18, padding: '10px' } }}  // Increase font size and padding
              InputLabelProps={{ style: { fontSize: 18 } }}  // Adjust label font size
            />
            <TextField
              label="Password"
              type="password"
              fullWidth
              margin="normal"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              inputProps={{ style: { fontSize: 18, padding: '10px' } }}  // Increase font size and padding
              InputLabelProps={{ style: { fontSize: 18 } }}  // Adjust label font size
            />
            <Button
              variant="contained"
              color="primary"
              type="submit"
              fullWidth
              style={{ marginTop: '20px', padding: '10px', fontSize: '18px' }}  // Adjust button size and text
            >
              Login
            </Button>
          </form>
          <Link href="/signup" style={{ marginTop: '20px', display: 'block', textAlign: 'center', fontSize: '16px' }}>
            Create an Account
          </Link>
        </Box>
      </Grid>
    </Container>
  );
};

export default Login;
