import React, { useEffect, useState } from 'react';
import { Button, Container, Typography, Box, Switch } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Dashboard = ({ darkMode, setDarkMode }) => {
  const [notes, setNotes] = useState([]);
  const navigate = useNavigate();

  // Fetch notes when the component mounts
  useEffect(() => {
    const fetchNotes = async () => {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/notes', {
        headers: { 'Authorization': `Bearer ${token}` },
      });
      const data = await response.json();
      setNotes(data);
    };

    fetchNotes();
  }, []);

  return (
    <Container sx={{ paddingTop: '20px', minHeight: '100vh' }}>
      {/* Header Section */}
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '10px 20px',
          backgroundColor: darkMode ? '#6a1b9a' : '#1976d2', // Purple in dark mode, blue in light mode
          borderRadius: '8px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',  // Subtle shadow for styling
          marginBottom: '20px',
          width: '80%', // 80% of the screen width
          marginLeft: 'auto',
          marginRight: 'auto', // Center the header horizontally
          color: '#ffffff', // White text in both modes
        }}
      >
        <Typography variant="h6">
          Jeremy's Notes App
        </Typography>
        <Box display="flex" alignItems="center">
          <Typography variant="body1" sx={{ marginRight: 1 }}>
            Dark Mode
          </Typography>
          <Switch checked={darkMode} onChange={() => setDarkMode(!darkMode)} />
        </Box>
      </Box>

      {/* Notes Section */}
      <Box sx={{ textAlign: 'center' }}>
        <Typography variant="h4" gutterBottom>Your Notes</Typography>

        {/* Button to create a new note */}
        <Button
          variant="contained"
          sx={{
            marginBottom: '20px',
            backgroundColor: darkMode ? '#6a1b9a' : '#1976d2', // Purple in dark mode, blue in light mode
            color: '#ffffff',
            '&:hover': {
              backgroundColor: darkMode ? '#5a1281' : '#115293',
            },
          }}
          onClick={() => navigate('/create')}
        >
          Add New Note
        </Button>

        {/* Notes Grid */}
        {notes.length > 0 ? (
          <Box sx={{ marginTop: '20px' }}>
            {notes.map((note) => (
              <Box
                key={note._id}
                sx={{
                  padding: '20px',
                  backgroundColor: 'var(--box-background-color)',
                  borderRadius: '8px',
                  boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
                  textAlign: 'left',
                  marginBottom: '10px',
                }}
              >
                <Typography variant="h6" sx={{ marginBottom: '10px' }}>
                  {note.title}
                </Typography>
                <Typography variant="body2" sx={{ marginBottom: '10px' }}>
                  {note.content}
                </Typography>
                <Button
                  variant="outlined"
                  onClick={() => navigate(`/edit/${note._id}`)}
                  sx={{ marginRight: '10px' }}
                >
                  Edit
                </Button>
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={async () => {
                    const token = localStorage.getItem('token');
                    await fetch(`http://localhost:5000/api/notes/${note._id}`, {
                      method: 'DELETE',
                      headers: {
                        'Authorization': `Bearer ${token}`,
                      },
                    });
                    setNotes(notes.filter((n) => n._id !== note._id)); // Remove the note from state
                  }}
                >
                  Delete
                </Button>
              </Box>
            ))}
          </Box>
        ) : (
          <Typography>You currently have no notes.</Typography>
        )}
      </Box>
    </Container>
  );
};

export default Dashboard;
