import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { AppBar, Toolbar, Typography, Container } from '@mui/material';
import Home from './components/Home';
import CreateNote from './components/CreateNote';
import Signup from './components/Signup';
import Login from './components/Login';
import PrivateRoute from './components/PrivateRoute';

const App = () => {
  const isLoggedIn = !!localStorage.getItem('token');  // Check if the user is logged in (if token exists)

  return (
    <Router>
      {/* Conditionally render AppBar if user is logged in */}
      {isLoggedIn && (
        <AppBar position="static">
          <Toolbar>
            <Container maxWidth="lg" style={{ display: 'flex', justifyContent: 'center' }}>
              <Typography variant="h6" align="center">
                Jeremy's Notes App
              </Typography>
            </Container>
          </Toolbar>
        </AppBar>
      )}

      {/* Main Content */}
      <Container maxWidth="lg" style={{ marginTop: isLoggedIn ? '40px' : '0' }}>
        <Routes>
          <Route path="/signup" element={<Signup />} />
          <Route path="/login" element={<Login />} />
          <Route path="/" element={<PrivateRoute><Home /></PrivateRoute>} />
          <Route path="/create" element={<PrivateRoute><CreateNote /></PrivateRoute>} />
        </Routes>
      </Container>
    </Router>
  );
};

export default App;
