import React, { useState } from 'react';
import { TextField, Button, Container, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const CreateNote = ({ darkMode, setDarkMode }) => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const navigate = useNavigate();

  const handleCreateNote = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem('token');  // Retrieve the token from local storage
    if (!token) {
      console.error('No token found');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/notes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,  // Pass the token in the Authorization header
        },
        body: JSON.stringify({ title, content }),  // Pass the title and content as request body
      });

      if (!response.ok) {
        throw new Error(`Error: ${response.statusText}`);
      }

      const data = await response.json();  // Parse the JSON response
      console.log('Note created:', data);  // Log the created note

      navigate('/');  // Navigate back to the dashboard after creation
    } catch (error) {
      console.error('Failed to create note:', error.message);  // Handle errors
    }
  };

  return (
    <Container sx={{ paddingTop: '20px', minHeight: '100vh' }}>
      <Typography variant="h4" gutterBottom>Create a New Note</Typography>
      <form onSubmit={handleCreateNote}>
        <TextField
          label="Title"
          fullWidth
          margin="normal"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <TextField
          label="Content"
          fullWidth
          multiline
          rows={4}
          margin="normal"
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
        <Button
          variant="contained"
          type="submit"
          sx={{
            backgroundColor: darkMode ? '#6a1b9a' : '#1976d2',
            color: '#ffffff',
            '&:hover': {
              backgroundColor: darkMode ? '#5a1281' : '#115293',
            },
          }}
        >
          Create Note
        </Button>
      </form>
    </Container>
  );
};

export default CreateNote;
