import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Layout from './components/Layout';  // Layout component to handle global structure
import Home from './components/Home';
import CreateNote from './components/CreateNote';
import EditNote from './components/EditNote';
import Signup from './components/Signup';
import Login from './components/Login';
import PrivateRoute from './components/PrivateRoute';  // Protect routes that require authentication

const App = () => {
  const [darkMode, setDarkMode] = useState(false);

  return (
    <Router>
      <Routes>
        <Route element={<Layout darkMode={darkMode} setDarkMode={setDarkMode} />}>
          <Route path="/signup" element={<Signup />} />
          <Route path="/login" element={<Login />} />
          <Route path="/" element={<PrivateRoute><Home darkMode={darkMode} setDarkMode={setDarkMode} /></PrivateRoute>} />
          <Route path="/create" element={<PrivateRoute><CreateNote darkMode={darkMode} setDarkMode={setDarkMode} /></PrivateRoute>} />
          <Route path="/edit/:id" element={<PrivateRoute><EditNote darkMode={darkMode} setDarkMode={setDarkMode} /></PrivateRoute>} />
        </Route>
      </Routes>
    </Router>
  );
};

export default App;
