import React, { useState } from 'react';
import { TextField, Button, Typography, Box, Link } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:5000/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password }),
    });

    const data = await response.json();
    if (response.ok) {
      localStorage.setItem('token', data.token);
      navigate('/');
    } else {
      console.error('Login failed:', data.message);
    }
  };

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',  // Ensure full viewport height
        width: '100vw',  // Ensure full viewport width
        backgroundColor: '#f0f0f0',
        overflow: 'hidden',  // Disable scrolling entirely
        padding: '0',  // Remove any padding
        margin: '0',  // Remove any margins
        boxSizing: 'border-box',  // Include padding and borders in the box model
      }}
    >
      {/* Header */}
      <Typography variant="h3" gutterBottom style={{ whiteSpace: 'nowrap' }}>  {/* Prevent wrapping */}
        Jeremy's Notes App
      </Typography>

      <Box
        sx={{
          width: '100%',
          maxWidth: '400px',
          padding: '20px',
          borderRadius: '8px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',  // Subtle shadow for styling
          backgroundColor: 'white',  // White background for the login box
          textAlign: 'center',  // Center the text
        }}
      >
        <Typography variant="h4" gutterBottom>
          Login
        </Typography>
        <form onSubmit={handleLogin}>
          <TextField
            label="Username"
            fullWidth
            margin="normal"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            inputProps={{ style: { fontSize: 18, padding: '10px' } }}  // Adjust input size
            InputLabelProps={{ style: { fontSize: 18 } }}  // Adjust label size
          />
          <TextField
            label="Password"
            type="password"
            fullWidth
            margin="normal"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            inputProps={{ style: { fontSize: 18, padding: '10px' } }}  // Adjust input size
            InputLabelProps={{ style: { fontSize: 18 } }}  // Adjust label size
          />
          <Button
            variant="contained"
            color="primary"
            type="submit"
            fullWidth
            style={{ marginTop: '20px', padding: '10px', fontSize: '18px' }}
          >
            Login
          </Button>
        </form>
        <Link href="/signup" style={{ marginTop: '20px', display: 'block', fontSize: '16px' }}>
          Create an Account
        </Link>
      </Box>
    </Box>
  );
};

export default Login;
