import React, { useEffect, useState } from 'react';
import { Button, Container, Typography, Grid, Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Dashboard = ({ darkMode, setDarkMode }) => {
  const [notes, setNotes] = useState([]);
  const navigate = useNavigate();

  // Fetch notes when the component mounts
  useEffect(() => {
    const fetchNotes = async () => {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/notes', {
        headers: { 'Authorization': `Bearer ${token}` },
      });
      const data = await response.json();
      setNotes(data);
    };

    fetchNotes();
  }, []);

  return (
    <Container sx={{ paddingTop: '20px', minHeight: '100vh' }}>
      {/* Header */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <Typography variant="h4">Your Notes</Typography>
        <Button 
          variant="contained" 
          color="primary" 
          onClick={() => navigate('/create')}
        >
          Add New Note
        </Button>
      </Box>

      {/* Notes Grid */}
      {notes.length > 0 ? (
        <Grid container spacing={4}>
          {notes.map((note) => (
            <Grid item xs={12} sm={6} md={4} key={note._id}>
              <Box
                sx={{
                  padding: '20px',
                  backgroundColor: 'var(--box-background-color)',
                  borderRadius: '8px',
                  boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
                  textAlign: 'left',
                }}
              >
                <Typography variant="h6" sx={{ marginBottom: '10px' }}>
                  {note.title}
                </Typography>
                <Typography variant="body2" sx={{ marginBottom: '10px' }}>
                  {note.content}
                </Typography>
                <Button 
                  variant="outlined" 
                  onClick={() => navigate(`/edit/${note._id}`)}
                  sx={{ marginRight: '10px' }}
                >
                  Edit
                </Button>
                <Button 
                  variant="contained" 
                  color="secondary"
                  onClick={async () => {
                    const token = localStorage.getItem('token');
                    await fetch(`http://localhost:5000/api/notes/${note._id}`, {
                      method: 'DELETE',
                      headers: {
                        'Authorization': `Bearer ${token}`,
                      },
                    });
                    setNotes(notes.filter((n) => n._id !== note._id)); // Remove the note from state
                  }}
                >
                  Delete
                </Button>
              </Box>
            </Grid>
          ))}
        </Grid>
      ) : (
        <Typography>You currently have no notes.</Typography>
      )}
    </Container>
  );
};

export default Dashboard;
