import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import { AppBar, Toolbar, Typography, Container } from '@mui/material';
import Home from './components/Home';
import CreateNote from './components/CreateNote';
import Signup from './components/Signup';
import Login from './components/Login';
import PrivateRoute from './components/PrivateRoute';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const location = useLocation();

  useEffect(() => {
    // Check if the user is logged in by checking for the token
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
  }, [location.pathname]);  // Update the login status whenever the route changes

  return (
    <Router>
      {/* Conditionally render AppBar only if not on login or signup pages */}
      {isLoggedIn && !['/login', '/signup'].includes(location.pathname) && (
        <AppBar position="static">
          <Toolbar>
            <Container maxWidth="lg" style={{ display: 'flex', justifyContent: 'center' }}>
              <Typography variant="h6" align="center">
                Jeremy's Notes App
              </Typography>
            </Container>
          </Toolbar>
        </AppBar>
      )}

      {/* Main Content */}
      <Container maxWidth="lg" style={{ marginTop: isLoggedIn ? '40px' : '0' }}>
        <Routes>
          <Route path="/signup" element={<Signup />} />
          <Route path="/login" element={<Login />} />
          <Route path="/" element={<PrivateRoute><Home /></PrivateRoute>} />
          <Route path="/create" element={<PrivateRoute><CreateNote /></PrivateRoute>} />
        </Routes>
      </Container>
    </Router>
  );
};

export default App;
